import React, { useState } from 'react'
import { View, StyleSheet } from 'react-native'
import { Text, Divider, Button  } from 'react-native-elements'
import FilterItem from '../components/FilterItem'
import { PickerView, } from '@ant-design/react-native'


const FiltersScreen = () => {   
    const [gluten, setGluten] = useState(false)
    const [lactose, setLactose] = useState(false)
    const [vegan, setVegan] = useState(false)
    const [vegeterian, setVegeterian] = useState(false)
    const [number, setNumber] = useState({})

    const durations = [[],[]]
    for (let index = 0; index < 13; index++) {
        durations[0].push({ label: `${index * 10} min`, value: `${index * 10}`})
        durations[1].push({ label: `${index * 10} min`, value: `${index * 10}`})
    }
    
    return (
        <View style={styles.container}>
            <Text style={styles.title}>Avaible Filters</Text>
            <View style={{ marginBottom: 25 }}/>
            <FilterItem 
                val={gluten}
                onChange={setGluten}
                title="Gluten-Free"
            />
            <FilterItem 
                val={lactose}
                onChange={setLactose}
                title="Lactose-Free"
            />
            <FilterItem 
                val={vegan}
                onChange={setVegan}
                title="Vegan"
            />
            <FilterItem 
                val={vegeterian}
                onChange={setVegeterian}
                title="Vegeterian"
            />
            <View style={{ marginTop: 10 }} />
            <Text>Select Time Range</Text>
            <View style={styles.picker}>
                <PickerView
                    onChange={(value) => setNumber(value)}
                    value={number}
                    data={durations}
                    cascade={false}
                    itemStyle={{ marginVertical: 7 }}
                />
            </View>
            
        </View>
    )
}

export default FiltersScreen

const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingTop: 30,
        paddingHorizontal: 30
    },
    title: {
        fontSize: 22,
        textAlign: 'left'
    },
    picker: {
        borderWidth: 1,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: 150,
        overflow: 'hidden'
    }
})
