import React, { useState } from 'react'
import { View, StyleSheet } from 'react-native'
import { Text, Divider, Button  } from 'react-native-elements'
import FilterItem from '../components/FilterItem'
import { PickerView } from '@ant-design/react-native'


const FiltersScreen = () => {   
    const [gluten, setGluten] = useState(false)
    const [lactose, setLactose] = useState(false)
    const [vegan, setVegan] = useState(false)
    const [vegeterian, setVegeterian] = useState(false)
    const [timeRange, setTimeRange] = useState(false)
    const [number, setNumber] = useState(null)

    const season = [
        {
          label: '春',
          value: '春',
        },
        {
          label: '夏',
          value: '夏',
        },
    ];

    const durations = []
    for (let index = 0; index < 13; index++) {
        durations.push({ label: `${index * 10} min`, value: index * 10})
    }
    
    console.log(durations)
    return (
        <View style={styles.container}>
            <Text style={styles.title}>Avaible Filters</Text>
            <View style={{ marginBottom: 25 }}/>
            <FilterItem 
                val={gluten}
                onChange={setGluten}
                title="Gluten-Free"
            />
            <FilterItem 
                val={lactose}
                onChange={setLactose}
                title="Lactose-Free"
            />
            <FilterItem 
                val={vegan}
                onChange={setVegan}
                title="Vegan"
            />
            <FilterItem 
                val={vegeterian}
                onChange={setVegeterian}
                title="Vegeterian"
            />
            <FilterItem 
                val={timeRange}
                onChange={setTimeRange}
                title="Select Time Range"
            />
            {timeRange && (
                <React.Fragment>
                    <View style={styles.picker}>
                        <PickerView
                            data={season}
                            cascade={false}
                            itemStyle={{ marginVertical: 7 }}
                        />
                    </View>
                    <Text></Text>
                </React.Fragment>
            )}
            
        </View>
    )
}

export default FiltersScreen

const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingTop: 30,
        paddingHorizontal: 30
    },
    title: {
        fontSize: 22,
        textAlign: 'left'
    },
    picker: {
        marginTop: 10,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: 150,
        overflow: 'hidden'
    }
})
