import React from 'react'
import { StyleSheet, TouchableOpacity } from 'react-native'
import { ListItem } from 'react-native-elements'

const CategoryItem = ({ item, navigation }) => {
    return (
        <TouchableOpacity 
            activeOpacity={0.5} 
            style={styles.item}
            onPress={() => navigation.navigate('Category', {
                id: item.id,
                title: item.title
            })}
        >
            <ListItem containerStyle={{ ...styles.itemContainer, backgroundColor: item.color }}>
                <ListItem.Title style={styles.title}>
                    { item.title }
                </ListItem.Title>
            </ListItem>
        </TouchableOpacity>
    )
}

export default CategoryItem

const styles = StyleSheet.create({
    item: {
        marginVertical: 10,
        borderRadius: 10,
    },
    title: {
        marginLeft: 15,
        fontSize: 20,
        fontFamily: 'open-sans-bold'
    },
    itemContainer: {
        height: 150,
        borderRadius: 10
    },  
})
