import React, { useState } from 'react'
import { View, StyleSheet, Switch, TouchableOpacity } from 'react-native'
import { Text, Button, Divider } from 'react-native-elements'

const FiltersScreen = () => {
    const [gluten, setGluten] = useState(false)
    const [lactose, setLactose] = useState(false)

    return (
        <View style={styles.container}>
            <Text style={styles.title}>Avaible Filters</Text>
            <View style={{ marginBottom: 30 }}/>
            <TouchableOpacity 
                style={styles.row}
                activeOpacity={0.8}
                onPress={() => setGluten(!gluten)}
            >
                <Text style={styles.text}>Gluten Free</Text>
                <Switch 
                    value={gluten}
                    onValueChange={(value) => setGluten(value)}
                />
            </TouchableOpacity>
            <Divider />
            <TouchableOpacity 
                style={styles.row}
                activeOpacity={0.8}
                onPress={() => setLactose(!lactose)}
            >
                <Text style={styles.text}>Lactose Free</Text>
                <Switch 
                    value={lactose}
                    onValueChange={(value) => setLactose(value)}
                />
            </TouchableOpacity>
        </View>
    )
}

export default FiltersScreen

const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingTop: 30,
        paddingHorizontal: 30
    },
    title: {
        fontSize: 22,
        textAlign: 'left'
    },
    row: {
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-between',
        marginVertical: 20,
    },
    text: {
        fontSize: 18,
    }
})
